@js:
// 设备类型检测
let device, devicetype;
try {
    devicetype = java.androidId();
    device = 'android';
} catch (e) {
    devicetype = java.deviceID();
    device = 'ios';
}

// 数据
let [itemId, book_id, book_name, author, thumb_url, media] = result.split("|==|");

// 获取密钥并构建 URL
let key;
try {
    key = source.getLoginInfoMap()['请输入密钥'];
} catch (error) {
    java.longToast('\n\n请先输入密钥\n密钥获取途径，书籍详情页，点击右上角点击登陆，获取后，填写，再次刷新详情页');
    key = '';  // 密钥为空
}

let catalogue_url = `${source.bookSourceUrl}/reader?item_id=${itemId}&book_id=${book_id}&tone_id=-1&key=${key}&device=${devicetype}&book_name=${book_name}&author=${author}&thumb_url=${thumb_url}&media=${media}`;

